mapper.select:
SELECT * FROM REPLY WHERE
ARTICLE_NO=#{article_no}
ORDER BY REPLY_NO ASC

SELECT ARTICLE_NO,
REPLY.REPLY_NO, ID, REPLY_LIKE FROM REPLY, REPLY_LIKE WHERE
REPLY.REPLY_NO=REPLY_LIKE.REPLY_NO AND ARTICLE_NO=#{article_no} AND ID=#{id}

mapper.update:
UPDATE REPLY SET REPLY_LIKE_CNT=REPLY_LIKE_CNT+1
WHERE
REPLY_NO=#{reply_no}

UPDATE REPLY SET REPLY_LIKE_CNT=REPLY_LIKE_CNT-1
WHERE
REPLY_NO=#{reply_no}

UPDATE REPLY SET REPLY_UNLIKE_CNT=REPLY_LIKE_CNT+1
WHERE
REPLY_NO=#{reply_no}

UPDATE REPLY SET REPLY_UNLIKE_CNT=REPLY_LIKE_CNT-1
WHERE
REPLY_NO=#{reply_no}
